import React,{useState} from "react"
import DatingCard from 'react-tinder-card'
import './Dating_cards.css'

const Dating_cards =()=>{
    const [people,setPeople]=useState([
        {name:"Julie",imgURL:"https://images.unsplash.com/photo-1555320818-21ebf0faf145?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1928&q=80"},
        {name:"Rose",imgURL:"https://images.unsplash.com/photo-1584720223124-466ff369e7c2?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxleHBsb3JlLWZlZWR8Nnx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=60"},
        {name:"John",imgURL:"https://images.unsplash.com/photo-1480429370139-e0132c086e2a?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1888&q=80"},
        {name:"Jack",imgURL:"https://media.istockphoto.com/id/1368477551/photo/young-smiling-athlete-in-public-park.jpg?s=1024x1024&w=is&k=20&c=gfaptwMzTgxbgEUQK7YpA81qZHu5DYJu6aWcsMKPzPo="},
    ]);
    const swiped=(direction,nameToDelete)=>{
        console.log("Receiving"+nameToDelete);
    };

    const outOfFrame=(name)=>{
        console.log(name+"left the screen");
    };
    return(
        <div className="Dating_cards">
            <div className="Dating_cards_container">
                {
                    people.map((person)=>(
                        <DatingCard className="swipe"
                            key={person.name}
                            preventSwipe={['up','down']}
                            onSwipe={(dir)=>swiped(dir,person.name)}
                            onCardLeftScreen={()=>outOfFrame(person.name)}>
                            <div style={{backgroundImage:`url(${person.imgURL})`}}className="cards">
                                <h3>{person.name}</h3>
                            </div>
                        </DatingCard>
                    ))
                }
            </div>
        </div>
    );
}
export default Dating_cards
