const db =[
        
            {name:"Julie",url:"https://images.unsplash.com/photo-1555320818-21ebf0faf145?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1928&q=80"},
            {name:"Rose",url:"https://images.unsplash.com/photo-1584720223124-466ff369e7c2?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxleHBsb3JlLWZlZWR8Nnx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=60"},
            {name:"John",url:"https://images.unsplash.com/photo-1480429370139-e0132c086e2a?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1888&q=80"},
            {name:"Jack",url:"https://media.istockphoto.com/id/1368477551/photo/young-smiling-athlete-in-public-park.jpg?s=1024x1024&w=is&k=20&c=gfaptwMzTgxbgEUQK7YpA81qZHu5DYJu6aWcsMKPzPo="},
        ]
        function Simple () {
            const characters = db
            const [lastDirection, setLastDirection] = useState()
          
            const swiped = (direction, nameToDelete) => {
              console.log('removing: ' + nameToDelete)
              setLastDirection(direction)
            }
          
            const outOfFrame = (name) => {
              console.log(name + ' left the screen!')
            }
          
            return (
              <div>
                <link href='https://fonts.googleapis.com/css?family=Damion&display=swap' rel='stylesheet' />
                <link href='https://fonts.googleapis.com/css?family=Alatsi&display=swap' rel='stylesheet' />
                <h1>React Tinder Card</h1>
                <div className='cardContainer'>
                  {characters.map((character) =>
                    <TinderCard className='swipe' key={character.name} onSwipe={(dir) => swiped(dir, character.name)} onCardLeftScreen={() => outOfFrame(character.name)}>
                      <div style={{ backgroundImage: 'url(' + character.url + ')' }} className='card'>
                        <h3>{character.name}</h3>
                      </div>
                    </TinderCard>
                  )}
                </div>
                {lastDirection ? <h2 className='infoText'>You swiped {lastDirection}</h2> : <h2 className='infoText' />}
              </div>
            )}
    
            export default TinderCard
